sys_script_include.script:
var AllCompanieswithinDescription = Class.create();
AllCompanieswithinDescription.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	AllCompanieswithinDescription: function(){
		var companies = new GlideRecord("core_company");
		companies.query();

		var arrayRecords = "";
		while(companies.next()){
			arrayRecords += companies.name +',';
		}

		return arrayRecords;
	},

    type: 'AllCompanieswithinDescription'
});
