var GetCompany = Class.create();
GetCompany.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getCompany: function() {
		var user = new GlideRecord("sys_user");
		var caller = this.getParameter('sysparm_userID');
		//user.addQuery('sys_id', caller);
		user.addEncodedQuery('sys_id='+ caller+'^company.customer=true');
		user.query();
		var array = "";
		if(user.next()){//SI EXISTE EL REGISTRO
			/*if(user.company.customer == true){// SI LA COMPANY ES COSTUMER
				return user.company;
			}*/
			return user.company;
		}
			return '';
	},
    type: 'GetCompany'
});
